//1 per piece
//spawns tiles in proper locations
//conains movement rotation functions


const CREATED_TILES = 'CREATED_TILES'

const createdTiles = tiles => {
    return {
        type: CREATED_TILES,
        tiles
    }
}


const tilesReducer = (state = [], action) => {
    switch (action.type) {
        case CREATED_TILES:
            return action.tiles
        default: 
            return state
    }    

}

export default tilesReducer
